import React, { useContext, useState } from "react";
import "./Backend.css";
import { ProductContext } from "../ProductContext";

const Backend = () => {
  const state = useContext(ProductContext);
  const list = state.products.map((item) => (
    <div key={item._id}>
      <li className="grey">name:{item.name}</li>
      <li className="blue">price:R{item.price}</li>
      <li className="red">Image URL:{item.image}</li>
      <li className="green">book id:{item._id}</li>
    </div>
  ));

  const [price, setPrice] = useState("");
  const [name, setName] = useState("");
  const [imageURL, setImageURL] = useState("");

  const handleChange = (e) => {
    setPrice({
      [e.target.name]: e.target.value,
    });
    setName({
      [e.target.name]: e.target.value,
    });
    setImageURL({
      [e.target.name]: e.target.value,
    });
  };
  const SubmitBook = (e) => {
    e.preventDefault();
    const addbook = {
      name: name,
      price: price,
      imageURL: imageURL,
    };
    console.log(addbook);
  };

  return (
    <div className="backend">
      <h2>Hello admin</h2>
      <div className="allbackend center">
        <div className="list">
          <h5>The raw list of all products</h5>
          <ul>{list}</ul>
        </div>
        <div className="addproduct">
          <form onSubmit={SubmitBook}>
            <fieldset>
              <legend>add a book</legend>
              <label htmlFor="price">price</label>
              <input
                type="number"
                name="price"
                id="price"
                value={price}
                onChange={handleChange}
              />
              <label htmlFor="name">name</label>
              <input
                type="text"
                name="name"
                id="name"
                value={name}
                onChange={handleChange}
              />
              <label htmlFor="imageURL">imageURL</label>
              <input
                type="text"
                name="imageURL"
                id="imageURL"
                value={imageURL}
                onChange={handleChange}
              />
              <button className="btn green">Add</button>
            </fieldset>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Backend;
